import {
  FETCH_POSTS_START,
  FETCH_POSTS_SUCCESS,
  FETCH_POSTS_FAILURE
} from 'actionTypes'
import {fetchPosts as fetchPostsApi} from 'api'

export const fetchPosts = () => async dispatch => {
  dispatch({
    type: FETCH_POSTS_START
  })
  try{
    const posts = await fetchPostsApi()
    dispatch({
      type: FETCH_POSTS_SUCCESS,
      payload: posts
    })
  } catch (err) {
    dispatch({
      type: FETCH_POSTS_FAILURE,
      payload: err,
      error: true
    })
  }

}