import {
  FETCH_POSTS_START,
  FETCH_POSTS_SUCCESS,
  FETCH_POSTS_FAILURE,
  FETCH_POST_BY_ID_START,
  FETCH_POST_BY_ID_SUCCESS,
  FETCH_POST_BY_ID_FAILURE
} from 'actionTypes'
import {fetchPosts as fetchPostsApi,
        fetchPostById as fetchPostByIdApi} from 'api'

export const fetchPosts = () => async dispatch => {
  dispatch({
    type: FETCH_POSTS_START
  })
  try {
    const posts = await fetchPostsApi()
    dispatch({
      type: FETCH_POSTS_SUCCESS,
      payload: posts
    })
  } catch (err) {
    dispatch({
      type: FETCH_POSTS_FAILURE,
      payload: err,
      error: true
    })
  }
}

export const fetchPostById = id => async dispatch => {
  dispatch({type: FETCH_POST_BY_ID_START})

  try {
    const post = await fetchPostByIdApi(id)
    dispatch({
      type: FETCH_POST_BY_ID_SUCCESS,
      payload: post
    })
  } catch (err) {
    dispatch({
      type: FETCH_POST_BY_ID_FAILURE,
      payload: err,
      error: true
    })
  }
}

