import * as R from 'ramda'

import posts from 'api/mockPosts'

export const fetchPosts = async () => {
  return new Promise((resolve, reject) => {
    resolve(posts)
  })
}

export const fetchPostById = async (id) => {
  return new Promise ((resolve, reject) => {
    const post = R.find(R.propEq('id', id), posts)
    resolve(post)
  })
}