import React, {Component} from 'react'

import './comment.css'

export default class Comment extends Component {
  render() {
    return (
      <div className="comment-wrap">
        <div className="user-icon">
          <img src={this.props.img} alt="user-icon"/>
        </div>
        <div className="comment-field">
          <p>{this.props.text}</p>
        </div>
      </div>
    )
  }
}